import React, { Component } from 'react'
import MediaPlayer from '../MediaPlayer/MediaPlayer'
import DummyStore from './DummyStore'


class Search extends Component {
    constructor() {
        super();
        this.state = {
            apiKey: 'AIzaSyCyDGjcu0UgCCIWV1ZRB2Tv-i3M9i1iaa0',
            searchURL: 'https://www.googleapis.com/youtube/v3/search',
            baseURL: 'https://www.youtube.com/watch?v=',
            response: null,
            value: '',
            maxResults: 10,
        }
        this.handleChange = this.handleChange.bind(this)
        // this.handleSubmit = this.handleSubmit.bind(this)

    }


    componentDidMount() {
        // this.getVideos('new music')
    }

    handleSubmit(query) {
            this.setState(
                { value: query,
                    response
                })
            this.getVideos(this.state.value, this.state.maxResults)
            console.log(this.state.value)
    }

    handleChange(event) {
        event.preventDefault()
        this.setState({ value: event.target.value })
        // console.log(this.state.value)
    }

    formatQueryParams(params) {
        const queryItems = Object.keys(params)
            .map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key])}`)
        return queryItems.join('&')
    }


    getVideos(query, maxResults) {
        const params = {
            key: this.state.apiKey,
            q: query,
            part: 'snippet',
            maxResults
        };
        const queryString = this.formatQueryParams(params)
        const url = this.state.searchURL + '?' + queryString;

        fetch(url)
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error(response.statusText);
            })
            .then(responseJson => this.setState({ response: responseJson }))
            .catch(err => {
                console.log(err.message)
            });
    }

    render() {


        return (
            <div className="Search">

                <form>
                    <input type="text" value={this.state.value} onChange={this.handleChange} />
                    <li onClick={() => this.handleSubmit(this.state.value)}>search</li>
                </form>

                <MediaPlayer
                    response={this.state.response}
                // response={DummyStore}
                />
            </div>
        )
    }

}

export default Search
