import React, { Component } from 'react'
import Decoder from '../Decoder/Decoder';
import '../MediaPlayer/MediaPlayer.css'

class MediaPlayer extends Component {

    constructor() {
        super();
        this.state = {
            nowPlaying: null,
            idx: -1,
        }
    }

    playVideo = (id, video) => {
        this.setState({ nowPlaying: id })
        console.log(video)
    }

    play = (args) => {
        console.log(args)
    }

    pause = (args) => {
        console.log(args)
        this.setState({ nowPlaying: null })
    }

    next = (args) => {
        console.log(args)
    }

    previous = (args) => {
        console.log(args)
    }
    render() {


        let queu = []
        if (this.props.response) {
            for (let i = 0; i < this.props.response.items.length; i++) {
                if (this.props.response.items[i].id.videoId) {
                    console.log(this.props.response.items[i].id.videoId)
                    queu.push(
                            {
                                title: this.props.response.items[i].snippet.title,
                                description: this.props.response.items[i].snippet.description,
                                thumbnails: this.props.response.items[i].snippet.thumbnails,
                                id: this.props.response.items[i].id.videoId,
                                idx: i
                            })
                }

            }

        }



        return (
            <div className="Results">
                <Decoder
                    videoId={this.state.nowPlaying}
                />
                {this.props.response && <div className="Result_List">
                    <div className="Controls">
                        <ul>
                            <li onClick={() => this.play('play')}>
                                >
                            </li>
                            <li onClick={() => this.pause('pause')}>
                                ||
                            </li>
                            <li onClick={() => this.previous('previous')}>
                                &lt;
                            </li>
                            <li onClick={() => this.next('next')}>
                                &gt;
                            </li>
                        </ul>
                    </div>
                    <div className="List">
                        <ul>
                            {queu.map(videos => <li key={Math.random() * Math.random()} onClick={() => this.playVideo(videos.id, videos.idx)}>{videos.title}</li>)}
                        </ul>
                    </div>
                </div>}
            </div>
        )
    }

}

export default MediaPlayer
