import React, { Component } from 'react';

class Decoder extends Component {

  constructor(props) {
    super(props);
    this.state = {
      // serverURL: 'https://frozen-thicket-30265.herokuapp.com/downloadmp3?url=',
      serverURL: 'http://localhost:8000/downloadmp3?url=',
      videoId: this.props.videoId,
    }
  }

  componentWillUpdate(){
    this.getDuration()
  }

  getDuration = () => {
    let audioElement = document.createElement('audio');
    audioElement.src = this.state.serverURL + this.state.videoId;
  }


  render() {   

    return (
      <div className="Media_Player">
        {this.props.videoId && <audio src={this.state.serverURL + this.props.videoId} controls autoPlay />}
      </div>
    );
  }
}

export default Decoder;
