import React, { Component } from 'react';

class Decoder extends Component {

  constructor(props) {
    super(props);
    this.state = {
      // serverURL: 'https://frozen-thicket-30265.herokuapp.com/downloadmp3?url=',
      serverURL: 'http://localhost:8000/downloadmp3?url=',
    }
  }

  componentDidMount() {
    this.setState({ interval: setInterval(this.listen4DB, 1000) })

  }

  duration = () => {
    if (this.props.videoId) {
      var audio = document.getElementById("audio-1");
      if (audio.readyState > 0) {
        var minutes = parseInt(audio.duration / 60, 10);
        var seconds = parseInt(audio.duration % 60);

        alert(minutes + ":" + seconds);
      }
    }

  }

  render() {

    return (
      <div className="Media_Player">
        {this.props.videoId && <audio id="audio-1" src={this.state.serverURL + this.props.videoId} controls autoPlay />}
      </div>
    );
  }
}

export default Decoder;
