import React, { Component } from 'react';

class Decoder extends Component {

  constructor(props) {
    super(props);
    this.state = {
      // serverURL: 'https://frozen-thicket-30265.herokuapp.com/downloadmp3?url=',
      serverURL: 'http://localhost:8000/downloadmp3?url=',
      videoId: this.props.videoId,
    }
  }

  componentWillUpdate(){
    this.getDuration()
  }

  getDuration = () => {
    sounds = document.getElementsByTagName('audio')[0];
    sounds.addEventListener('playing', function () {
        $('.header h2').empty();
        $('.header h2').append(`LIVE`);
        $(`#${id}state`).empty();
        $(`#${id}state`).append(`
        <div class='lds-facebook'>
        <div>
        </div>
        <div>
        </div>
        <div>
        </div>
        </div>`);
    }, true);
  }


  render() {   

    return (
      <div className="Media_Player">
        {this.props.videoId && <audio src={this.state.serverURL + this.props.videoId} controls autoPlay />}
      </div>
    );
  }
}

export default Decoder;
