import React, { Component } from 'react'
import Decoder from '../Decoder/Decoder';
import '../MediaPlayer/MediaPlayer.css'

class MediaPlayer extends Component {

    constructor() {
        super();
        this.state = {
            nowPlaying: null,
            queu: null,
            waiting4DB: true,
            interval1: '',
            idx: 0,
            pause: false,
            trackDuration: '',
            timer: 0

        }
    }

    componentDidMount() {
        this.setState({ interval1: setInterval(this.listen4DB, 1000) })
    }

    playTrack = (args) => {
        this.setState({ nowPlaying: this.state.queu[this.state.idx].id })
        console.log(this.state.idx + ': ' + this.state.queu[this.state.idx].title)
    }

    pause = () => {
    var audio = document.getElementById("audio");
}

    next = () => {
        if (this.state.idx < this.state.queu.length) {
            this.setState({ idx: this.state.idx + 1 })
            this.playTrack()
        }else{
            this.setState({idx: 0})
        }
    }

    previous = () => {
        if(this.state.idx > 0){
        this.setState({ idx: this.state.idx -1 })
        this.playTrack()
        }else{
            this.setState({idx: this.state.queu.length - 1})
        }
    }

    listen4DB = () => {
        if (this.props.response) {
            this.setState({ waiting4DB: false })
            this.stopListening4DB()
        }
    }

    stopListening4DB = () => {
        clearInterval(this.state.interval1);
        this.buildQueu()
    }


    buildQueu = () => {
        let temp = []
        if (this.props.response) {
            for (let i = 0; i < this.props.response.items.length; i++) {
                if (this.props.response.items[i].id.videoId) {
                    temp.push(
                        {
                            title: this.props.response.items[i].snippet.title,
                            description: this.props.response.items[i].snippet.description,
                            thumbnails: this.props.response.items[i].snippet.thumbnails,
                            id: this.props.response.items[i].id.videoId,
                        })
                }
            }
            this.setState({ queu: temp })
        }
    }

    render() {

        return (
            <div className="Results">

                <div className="Result_List">
                    {this.state.queu && <div className="Controls">
                        {/* <section>
                            <h1>
                                {this.state.queu[this.state.idx].title}
                            </h1>
                            <br>
                            </br>
                            <p>
                                {this.state.queu[this.state.idx].description}
                            </p>
                        </section> */}
                        <br>
                        </br>
                        <ul>
                            <li onClick={() => this.previous()}>
                                &lt;
                            </li>
                            {!this.state.nowPlaying && <li onClick={() => this.playTrack(this.state.queu[this.state.idx].id)}>
                                play
                            </li>}
                            {this.state.nowPlaying && <li onClick={() => this.pause()}>
                                pause
                            </li>}
                            <li onClick={() => this.next()}>
                                &gt;
                            </li>
                        </ul>
                    </div>}
                    <br></br>
                    <div className="List">
                        <ul>
                            {this.state.queu && this.state.queu.map(videos =>
                                <li key={Math.random() * Math.random()} onClick={() => this.playTrack(videos)}>{videos.title}
                                </li>)}
                        </ul>
                    </div>
                </div>
                <Decoder
                    videoId={this.state.nowPlaying}
                />
            </div>
        )
    }

}

export default MediaPlayer
