import React, { Component } from 'react';
    var jsmediatags = require("jsmediatags");


class Decoder extends Component {

  constructor(props) {
    super(props);
    this.state = {
      // serverURL: 'https://frozen-thicket-30265.herokuapp.com/downloadmp3?url=',
      serverURL: 'http://localhost:8000/downloadmp3?url=',
      videoId: this.props.videoId,
    }
  }



  render() {



    if(this.state){

    }
    new jsmediatags.Reader(this.state.serverURL + this.props.videoId)
      .setTagsToRead(["title", "artist"])
      .read({
        onSuccess: function(tag) {
          console.log(tag);
        },
        onError: function(error) {
          console.log(':(', error.type, error.info);
        }
      });


    return (
      <div className="Media_Player">
        {this.props.videoId && <audio id="myAudio" src={this.state.serverURL + this.props.videoId} controls autoPlay />}
      </div>
    );
  }
}

export default Decoder;
